package com.example.miniprogect1.Service;import com.example.miniprogect1.domain.Ilchon;import com.example.miniprogect1.domain.Ilchonpatch;import com.example.miniprogect1.repository.IlchonRepository;import com.example.miniprogect1.repository.IlchonpatchRepository;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class IlchonService {    private final IlchonRepository ilchonRepository;    private final IlchonpatchRepository ilchonpatchRepository;    public IlchonService(IlchonRepository ilchonRepository, IlchonpatchRepository ilchonpatchRepository) {        this.ilchonRepository = ilchonRepository;        this.ilchonpatchRepository = ilchonpatchRepository;    }    public Ilchon saveIlchon(Ilchon ilchon) {        return ilchonRepository.save(ilchon);    }    public Ilchonpatch saveIlchonpatch(Ilchonpatch ilchonpatch) {        return ilchonpatchRepository.save(ilchonpatch);    }    public List<Ilchonpatch> findIlchonpatchesByRequester(String requesterName) {        return ilchonpatchRepository.findByRequester_UserNickName(requesterName);    }    public List<Ilchonpatch> findIlchonpatchesByRequesterAndReceiver(String requesterName, String receiverName) {        return ilchonpatchRepository.findByRequester_UserNickNameAndReceiver_UserNickName(requesterName, receiverName);    }    public List<Ilchon> findIlchonsByUser(String userName) {        return ilchonRepository.findByMe_UserNickNameOrYou_UserNickName(userName, userName);    }}