package com.example.miniprogect1.repository;import com.example.miniprogect1.domain.Ilchonpatch;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface IlchonpatchRepository extends JpaRepository<Ilchonpatch, Long> {    List<Ilchonpatch> findByRequester_UserNickName(String requesterName);    List<Ilchonpatch> findByRequester_UserNickNameAndReceiver_UserNickName(String requesterName, String receiverName);    @Query(value = "select *" +            "   from ilchonpatch" +            "   where requester_user_nickname = :requesterName" +            "   limit 1", nativeQuery = true)    Ilchonpatch findFirstByRequester_UserNickName(@Param("requesterName") String requesterName);}